// components/Dashboard.js atualizado v2
import React from 'react';
import SensorCard from './SensorCard';
import './Dashboard.css';

const Dashboard = ({ data, darkMode }) => {
  // Determinar os níveis de alerta para cada sensor
  const tempAlertLevel = data.temperatura > 30 ? 'high' : 'none';

  // Determinar níveis de alerta para umidade
  let humidityAlertLevel = 'none';
  if (data.umidade < 30) {
    humidityAlertLevel = 'high';
  } else if (data.umidade < 50) {
    humidityAlertLevel = 'medium';
  }

  // Determinar níveis de alerta para luminosidade
  let lightAlertLevel = 'none';
  if (data.luminosidade < 100) {
    lightAlertLevel = 'low';
  } else if (data.luminosidade > 1000) {
    lightAlertLevel = 'high';
  }

  return (
    <div className={`dashboard ${darkMode ? 'dark-mode' : ''}`}>      
      <div className="dashboard-grid">
        <SensorCard 
          title="Temperatura" 
          value={data.temperatura} 
          unit="°C" 
          icon="temperature"
          color="#FF5733"
          alertLevel={tempAlertLevel}
          alertMessage={tempAlertLevel === 'high' ? "Temperatura alta" : null}
          darkMode={darkMode}
        />
        <SensorCard 
          title="Luminosidade" 
          value={data.luminosidade} 
          unit="lux" 
          icon="light"
          color="#FFC300"
          alertLevel={lightAlertLevel}
          alertMessage={
            lightAlertLevel === 'low' 
              ? "Pouca luz" 
              : lightAlertLevel === 'high' 
                ? "Muita luz" 
                : null
          }
          darkMode={darkMode}
        />
        <SensorCard 
          title="Umidade" 
          value={data.umidade} 
          unit="%" 
          icon="humidity"
          color="#33A1FF"
          alertLevel={humidityAlertLevel}
          alertMessage={
            humidityAlertLevel === 'high' 
              ? "Umidade crítica" 
              : humidityAlertLevel === 'medium' 
                ? "Umidade baixa" 
                : null
          }
          darkMode={darkMode}
        />
      </div>
    </div>
  );
};

export default Dashboard;
